
public class P32 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char aVar = 'A';
		System.out.println("aVar : " + aVar);
		System.out.printf("aVar : %c\n" , aVar);
		System.out.println("aVar : " + (int)aVar);
		System.out.printf("aVar : %d\n" , (int)aVar);
		int bVar = 66;
		System.out.println("bVar : " + (char)bVar);
		System.out.printf("bVar : %s%n", (char)bVar);
		
		
	// %b	 boolean 형식으로 출력 		 
	// %d	 정수 형식으로 출력
	// %o	 8진수 정수의 형식으로 출력
	// %x 또는 %X		 16진수 정수의 형식으로 출력
	// %f	 소수점 형식으로 출력
	// %c	 문자형식으로 출력 
	// %s	 문자열 형식으로 출력
	// %n	 줄바꿈 기능
	// %e 또는 %E	 	지수 표현식의 형식으로 출력
	}
}